import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import { getSearchResult } from '../store/hackerResults/actions'

function HackerSearch (props) {
    const [term, setTerm] = useState('ransomware')
    const [debounceTerm, setDebounceTerm] = useState(term)

    useEffect(() => {
        const timerId = setTimeout(() => {
            setDebounceTerm(term)
        }, 1000)

        return () => {
            clearTimeout(timerId)
        }
    }, [term])

    useEffect(() => {

        if (debounceTerm) {
            props.getSearchResult(debounceTerm)
        }

    }, [debounceTerm])

    const renderResults = () => {
        if (props.results) {
            return props.results.map((result) => {
                console.log(result)
                return (
                    <div key={result.created_at_i} className="item">
                        <div className="content">
                            <div className="ui grid">
                                <div className="sixteen wide column">
                                    <a className="header" href={result.url}>{result.title}</a>

                                    <p className="description"><strong>Points:</strong> {result.points} | <strong>Author:</strong> {result.author}</p>
                                </div>
                            </div>
                        </div>
                    </div>
                )
            })
        }
    }

    console.log(props.results)

    
    return (
        <div>
            <div className="ui form">
                <div className="field">
                    <label>Enter Search Term:</label>
                    <input className="input" onChange={e => setTerm(e.target.value)} value={term} />
                </div>
            </div>

            <div className="ui raised very padded celled list segment">{renderResults()}</div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        loading: state.results.loading,
        results: state.results.results
    }
}

export default connect (mapStateToProps, { getSearchResult })(HackerSearch)
